<nav
	class="transition-all duration-300 ease-in-out relative border-r border-[#eaeaea] bg-white h-[1023px]"
	class:w-[237px]={isExpanded}
	class:w-[80px]={!isExpanded}
>
	<!-- Logo -->
	<div class="px-[30px] py-[19px] flex items-center justify-center">
		<div class="text-[#161b26] text-[32px] font-bold font-['Almarena Neue']">
			{#if isExpanded}
				<a href="/dashboard"> Faxility </a>
			{:else}
				F
			{/if}
		</div>
	</div>

	<!-- Navigation Items -->
	<div class="flex flex-col gap-5 px-[19px] mt-[100px]">
		{#each navItems as item}
			<button
				onclick={() => handleNavClick(item.link)}
				class="group flex items-center pl-[15px] pr-10 py-4 rounded-[14px] w-full transition-all duration-200
          {$page.url.pathname === item.link
					? 'bg-[#ed6a3e]/10 shadow-[-3px_0px_0px_0px_rgba(237,106,62,1.00)]'
					: 'bg-white/5 hover:bg-gray-50'}"
			>
				<div class="w-[17px] h-[17px] relative overflow-hidden" />
				{@html item.icon}
				{#if isExpanded}
					<div
						class="text-[13px] font-['Inter'] ml-[15px] whitespace-nowrap transition-all duration-200"
						class:text-[#2c333f]={item.isActive}
						class:text-[#575b67]={!item.isActive}
						class:font-semibold={item.isActive}
						class:font-medium={!item.isActive}
					>
						{item.name}
					</div>
				{:else}
					<div
						class="absolute left-[60px] bg-gray-800 text-white px-2 py-1 rounded text-sm scale-0 group-hover:scale-100 transition-all duration-200 z-20"
					>
						{item.name}
					</div>
				{/if}
			</button>
		{/each}
	</div>
</nav>

