<div class="flex">
	<Sidebar
		isExpanded={sidebarExpanded}
		{handleSidebarToggle}
		on:navigate={handleNavigation}
		on:logout={handleLogout}
	/>

	<main class="flex-1 flex-col">
		<Mainnav isExpanded={sidebarExpanded} {handleSidebarToggle} />
		<!-- buttons -->

		<div class="flex flex-col mx-[30px]">
			<div class="flex flex-wrap justify-start gap-x-[17px] gap-y-[20px]">
				{#each buttonList as button}
					<button
						class={`h-[54px] px-[18px] py-4 rounded-xl justify-start items-center gap-3 inline-flex overflow-hidden`}
						style="background-color: {button.color}1A"
					>
						<div class="h-[22px] relative overflow-hidden">
							{@html button.icon}
						</div>
						<div
							class="text-base font-semibold font-['Almarena Neue'] leading-snug whitespace-nowrap"
							style="color: {button.color}"
						>
							{button.name}
						</div>
					</button>
				{/each}
			</div>
		</div>

		<!-- Calendar -->
		<div class="px-6 py-4 mb-8 h-[1100px] rounded-lg border">
			<Calendar />
		</div>

		<!-- Site Map -->
		<div class="flex flex-col px-6">
			<span class="font-2xl font-bold"> Site Map </span>
		</div>

		<!-- Urgent Tasks -->

		<!-- Your main content here -->
	</main>
</div>
